A = ""
input = A.split("").map(&:to_i)
new_input = []

100.times do |time|
  len = input.length
  len.times do |l|
    l = l+1  
    val = input[l-1..-1].each_slice(l*2).to_a.each_with_index.map {|a,i| b = a[0..l-1].inject(:+); i.even? ? b : -b}.inject(:+) 
    new_input << val.abs % 10
  end
  input = new_input.clone
  new_input = []
end

puts input[0..7].map(&:to_s).join